import React from 'react'
import {Switch, Route} from 'react-router-dom'
import GlobalFeed from 'pages/globalFeed'
import Article from 'pages/article'

export default () => {
  return (
    <Switch>
      <Route path="/">
        <GlobalFeed />
      </Route>
      <Route path="/articles/:slug">
        <Article />
      </Route>
    </Switch>
  )
}